// Enhanced: Add execution metadata and rate limiting check
const executionStartTime = Date.now();
const clientIP = $input.item.json.headers['x-forwarded-for'] || $input.item.json.headers['x-real-ip'] || 'unknown';

// Rate limiting: Check if enabled
const rateLimitEnabled = $env.RATE_LIMIT_ENABLED === 'true';
const rateLimit = parseInt($env.RATE_LIMIT_PER_MINUTE) || 60;

return {
  ...($input.item.json),
  _metadata: {
    execution_start_time: executionStartTime,
    client_ip: clientIP,
    rate_limit_enabled: rateLimitEnabled,
    workflow_version: '1.1.0-enhanced',
    module: 'intake_lead_capture'
  }
};

// Enhanced: Provide detailed validation error messages
const body = $input.item.json.body || {};
const errors = [];

if (!body.name || body.name.length < 2) {
  errors.push('name (minimum 2 characters required)');
}

if (!body.email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(body.email)) {
  errors.push('email (valid format required)');
} else if (body.email.length > 320) {
  errors.push('email (maximum 320 characters)');
}

if (!body.phone || body.phone.length < 7) {
  errors.push('phone (minimum 7 digits required)');
}

const executionTime = Date.now() - ($input.item.json._metadata?.execution_start_time || Date.now());

return {
  success: false,
  error: `Missing or invalid required fields: ${errors.join(', ')}`,
  timestamp: new Date().toISOString(),
  execution_time_ms: executionTime,
  validation_failed: true
};

// Enhanced: Normalize and enrich data before CRM upsert
const body = $input.item.json.body;
const metadata = $input.item.json._metadata;

// Split name into first/last (improved logic)
const nameParts = body.name.trim().split(/\s+/);
const firstName = nameParts[0] || '';
const lastName = nameParts.length > 1 ? nameParts.slice(1).join(' ') : '';

// Normalize phone number (remove non-digits for storage)
const phoneNormalized = body.phone.replace(/\D/g, '');

// Calculate lead score based on interest and referral source
const interestScores = {
  'urgent': 10,
  'emergency': 10,
  'immediate': 10,
  'consultation': 7,
  'appointment': 7,
  'general': 5,
  'inquiry': 3,
  'information': 2
};

const referralScores = {
  'physician referral': 10,
  'patient referral': 9,
  'google search': 7,
  'google ads': 6,
  'facebook': 5,
  'instagram': 5,
  'direct': 4,
  'other': 3
};

const interestLower = (body.interest || '').toLowerCase();
const referralLower = (body.referral_source || '').toLowerCase();

let leadScore = 5; // Base score
for (const [key, score] of Object.entries(interestScores)) {
  if (interestLower.includes(key)) {
    leadScore = Math.max(leadScore, score);
    break;
  }
}

for (const [key, score] of Object.entries(referralScores)) {
  if (referralLower.includes(key)) {
    leadScore += Math.floor(score / 2);
    break;
  }
}

leadScore = Math.min(leadScore, 10);

return {
  ...($input.item.json),
  body: {
    ...body,
    first_name: firstName,
    last_name: lastName,
    phone_normalized: phoneNormalized,
    phone_display: body.phone,
    lead_score: leadScore,
    interest: body.interest || 'General Inquiry',
    referral_source: body.referral_source || 'Direct'
  }
};

