// Enhanced: Calculate execution metrics and build comprehensive response
const executionEndTime = Date.now();
const executionStartTime = $input.item.json._metadata?.execution_start_time || executionEndTime;
const executionDuration = executionEndTime - executionStartTime;

return {
  success: true,
  message: 'Lead captured successfully',
  data: {
    contact_id: $input.item.json.id || $input.item.json.vid || null,
    email: $input.item.json.body?.email || $input.item.json.email,
    name: $input.item.json.body?.name || $input.item.json.name,
    lead_score: $input.item.json.body?.lead_score || null,
    timestamp: new Date().toISOString()
  },
  metadata: {
    crm_updated: true,
    notification_sent: true,
    stored: true,
    execution_time_ms: executionDuration,
    workflow_version: '1.1.0-enhanced'
  },
  performance: {
    duration_ms: executionDuration,
    duration_category: executionDuration < 1000 ? 'fast' : executionDuration < 2000 ? 'normal' : 'slow'
  }
};
