parameters.jsCode:
// Generate unique session ID and prepare telehealth platform request
const appointmentId = $input.first().json.body.appointment_id;
const scheduledTime = $input.first().json.body.scheduled_time;
const patientName = $input.first().json.body.patient_name || 'Patient';
const patientEmail = $input.first().json.body.patient_email;
const providerName = $input.first().json.body.provider_name || $env.DEFAULT_PROVIDER_NAME || 'Provider';
const serviceType = $input.first().json.body.service_type || 'Telehealth Consultation';

// Generate unique session identifier
const sessionId = `${$env.CLINIC_ID || 'clinic'}_${appointmentId}_${Date.now()}`;

// Calculate session duration (from env or default 30 minutes)
const duration = parseInt($env.DEFAULT_SESSION_DURATION) || 30;

// Calculate start time (scheduled appointment time)
const startTime = DateTime.fromISO(scheduledTime);

// Session metadata
const sessionData = {
  session_id: sessionId,
  appointment_id: appointmentId,
  topic: `${serviceType} - ${patientName}`,
  start_time: startTime.toISO(),
  duration: duration,
  timezone: $env.CLINIC_TIMEZONE || 'America/New_York',
  patient: {
    name: patientName,
    email: patientEmail
  },
  provider: {
    name: providerName,
    email: $env.PROVIDER_EMAIL || $env.CLINIC_EMAIL
  },
  settings: {
    waiting_room: $env.ENABLE_WAITING_ROOM === 'true',
    recording: $env.ENABLE_AUTO_RECORDING === 'true',
    join_before_host: $env.ALLOW_JOIN_BEFORE_HOST === 'true',
    password_required: $env.REQUIRE_SESSION_PASSWORD === 'true'
  }
};

return sessionData;

