parameters.jsCode:
// Enhanced Validation with PHI-Safe Error Messages
const body = $input.first().json.body || {};
const errors = [];

// Appointment confirmation check
if (body.appointment_confirmed === false) {
  errors.push('appointment_confirmed: must be true to create session');
}

// Appointment ID validation
const appointmentId = body.appointment_id || body.data?.appointment_id;
if (!appointmentId || appointmentId.trim().length === 0) {
  errors.push('appointment_id: required');
} else if (appointmentId.length > 100) {
  errors.push('appointment_id: maximum 100 characters');
}

// Patient email validation
const email = body.patient_email || body.data?.patient_email;
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!email || !emailRegex.test(email)) {
  errors.push('patient_email: required and must be valid format');
} else if (email.length > 320) {
  errors.push('patient_email: maximum 320 characters');
}

// Patient name validation
const name = body.patient_name || body.data?.patient_name;
if (!name || name.trim().length < 2) {
  errors.push('patient_name: required, minimum 2 characters');
} else if (name.length > 100) {
  errors.push('patient_name: maximum 100 characters');
}

// Scheduled time validation
const scheduledTime = body.scheduled_time || body.data?.scheduled_time;
if (!scheduledTime) {
  errors.push('scheduled_time: required');
} else {
  try {
    const parsedTime = DateTime.fromISO(scheduledTime);
    if (!parsedTime.isValid) {
      errors.push('scheduled_time: must be valid ISO 8601 format');
    }
  } catch (e) {
    errors.push('scheduled_time: must be valid ISO 8601 format');
  }
}

// Duration validation (optional, use default if missing)
const duration = body.duration_minutes || body.data?.duration_minutes || parseInt($env.DEFAULT_SESSION_DURATION) || 30;
if (duration < 5 || duration > 240) {
  errors.push('duration_minutes: must be between 5 and 240');
}

// Contact ID validation (optional but recommended)
const contactId = body.contact_id || body.data?.contact_id;

if (errors.length > 0) {
  return {
    validation_passed: false,
    errors: errors,
    error_count: errors.length
  };
}

return {
  validation_passed: true,
  validated_data: {
    appointment_id: appointmentId.trim(),
    patient_email: email.toLowerCase().trim(),
    patient_name: name.trim(),
    patient_phone: body.patient_phone || body.data?.patient_phone || '',
    scheduled_time: scheduledTime,
    duration_minutes: duration,
    timezone: body.timezone || body.data?.timezone || $env.CLINIC_TIMEZONE || 'America/New_York',
    service_type: body.service_type || body.data?.service_type || 'Telehealth Consultation',
    provider_name: body.provider_name || body.data?.provider_name || $env.DEFAULT_PROVIDER_NAME || 'Provider',
    provider_email: body.provider_email || $env.PROVIDER_EMAIL || $env.CLINIC_EMAIL,
    contact_id: contactId || null,
    booking_id: body.booking_id || body.data?.booking_id || appointmentId
  }
};

// PHI Masking for Logs (Level 2)
const validatedData = $input.first().json.validated_data;

function maskEmail(email) {
  if (!email || !email.includes('@')) return email;
  const [local, domain] = email.split('@');
  const maskedLocal = local.charAt(0) + '***' + (local.length > 1 ? local.charAt(local.length - 1) : '');
  return maskedLocal + '@' + domain;
}

function maskPhone(phone) {
  if (!phone) return phone;
  const digits = phone.replace(/\D/g, '');
  if (digits.length < 4) return '****';
  return phone.replace(digits, '*'.repeat(digits.length - 4) + digits.slice(-4));
}

function maskName(name) {
  if (!name) return name;
  const parts = name.split(' ');
  return parts.map(part => part.charAt(0) + '***').join(' ');
}

return {
  ...validatedData,
  // Masked versions for logging
  patient_email_masked: maskEmail(validatedData.patient_email),
  patient_phone_masked: maskPhone(validatedData.patient_phone),
  patient_name_masked: maskName(validatedData.patient_name)
};

// Generate unique session ID and prepare telehealth platform request
const data = $input.first().json;
const appointmentId = data.appointment_id;
const scheduledTime = data.scheduled_time;
const patientName = data.patient_name;
const patientEmail = data.patient_email;
const providerName = data.provider_name;
const serviceType = data.service_type;
const duration = data.duration_minutes;
const timezone = data.timezone;

// Generate unique session identifier
const clinicId = $env.CLINIC_ID || 'clinic';
const timestamp = Date.now();
const sessionId = `${clinicId}_${appointmentId}_${timestamp}`;

// Calculate start time
const startTime = DateTime.fromISO(scheduledTime);

// Session security settings
const settings = {
  waiting_room: $env.ENABLE_WAITING_ROOM !== 'false', // Default true for HIPAA
  recording: $env.ENABLE_AUTO_RECORDING === 'true',
  join_before_host: $env.ALLOW_JOIN_BEFORE_HOST === 'true',
  password_required: $env.REQUIRE_SESSION_PASSWORD !== 'false', // Default true
  encryption: 'enhanced_encryption',
  auto_admit: false // Manual admission for HIPAA compliance
};

// Session metadata (for audit trail)
const sessionData = {
  session_id: sessionId,
  appointment_id: appointmentId,
  booking_id: data.booking_id,
  topic: `${serviceType} - ${patientName}`,
  start_time: startTime.toISO(),
  duration: duration,
  timezone: timezone,
  patient: {
    name: patientName,
    email: patientEmail,
    phone: data.patient_phone
  },
  provider: {
    name: providerName,
    email: data.provider_email
  },
  settings: settings,
  metadata: {
    contact_id: data.contact_id,
    workflow_version: $env.WORKFLOW_VERSION || '1.1.0-enhanced',
    created_at: new Date().toISOString()
  }
};

return sessionData;

