// Extract session URLs and format for patient/provider
const response = $input.first().json;
const sessionData = $('Prepare Session Data').first().json;
const maskedData = $('PHI Masking for Logs').first().json;

// Platform-specific URL extraction
let sessionLink, hostLink, sessionPassword, platformMeetingId;

const provider = $env.TELEHEALTH_PROVIDER_NAME || 'Generic';

if (provider === 'Zoom') {
  sessionLink = response.join_url;
  hostLink = response.start_url;
  sessionPassword = response.password || response.encrypted_password || null;
  platformMeetingId = response.id;
} else if (provider === 'Doxy.me') {
  sessionLink = `https://doxy.me/${response.room_name}`;
  hostLink = `https://doxy.me/${response.room_name}?provider=true`;
  sessionPassword = response.pin || null;
  platformMeetingId = response.room_id || response.room_name;
} else if (provider === 'Amwell') {
  sessionLink = response.patient_url;
  hostLink = response.provider_url;
  sessionPassword = null; // Token-based
  platformMeetingId = response.visit_id;
} else {
  // Generic fallback
  sessionLink = response.join_url || response.patient_url || response.url;
  hostLink = response.start_url || response.provider_url || response.host_url;
  sessionPassword = response.password || response.pin || null;
  platformMeetingId = response.id || response.meeting_id || response.visit_id;
}

// Recording URL (if auto-recording enabled)
const recordingUrl = response.recording_url || null;

// Session expiration (for auto-cleanup)
const startTime = DateTime.fromISO(sessionData.start_time);
const expiresAt = startTime.plus({ days: parseInt($env.SESSION_LINK_EXPIRY_DAYS) || 1 }).toISO();

// Format response with PHI masking for logs
return {
  session_id: sessionData.session_id,
  appointment_id: sessionData.appointment_id,
  booking_id: sessionData.booking_id,
  platform_meeting_id: platformMeetingId,
  session_link: sessionLink,
  host_link: hostLink,
  session_password: sessionPassword,
  recording_url: recordingUrl,
  scheduled_time: sessionData.start_time,
  duration: sessionData.duration,
  timezone: sessionData.timezone,
  expires_at: expiresAt,
  // Full data (for video platform and direct patient communication)
  patient_email: sessionData.patient.email,
  patient_name: sessionData.patient.name,
  patient_phone: sessionData.patient.phone,
  provider_name: sessionData.provider.name,
  provider_email: sessionData.provider.email,
  // Masked data (for logs and staff notifications)
  patient_email_masked: maskedData.patient_email_masked,
  patient_name_masked: maskedData.patient_name_masked,
  patient_phone_masked: maskedData.patient_phone_masked,
  provider: provider,
  created_at: sessionData.metadata.created_at,
  contact_id: sessionData.metadata.contact_id,
  workflow_version: sessionData.metadata.workflow_version
};

