parameters.jsCode:
// Normalize billing data and prepare for payment gateway
const input = $input.first().json.body;

// Extract customer info
const customerEmail = input.patient_email || input.customer_email;
const customerName = input.patient_name || input.customer_name || 'Customer';
const customerId = input.customer_id || null;

// Billing details
const amount = parseInt(input.amount);
const currency = (input.currency || $env.DEFAULT_CURRENCY || 'USD').toUpperCase();
const serviceCode = input.service_code;
const serviceDescription = input.service_description || input.service_type || 'Service Rendered';

// Membership (if applicable)
const membershipEnabled = $env.MEMBERSHIP_ENABLED === 'true';
const planId = input.plan_id || null;

// Generate invoice reference
const invoiceRef = `INV-${Date.now()}-${serviceCode}`;

// Session metadata
const sessionId = input.session_id || null;
const scheduledTime = input.scheduled_time || null;

return {
  customer: {
    id: customerId,
    email: customerEmail,
    name: customerName,
    phone: input.patient_phone || input.customer_phone || null
  },
  billing: {
    amount: amount,
    currency: currency,
    service_code: serviceCode,
    service_description: serviceDescription,
    invoice_reference: invoiceRef
  },
  membership: {
    enabled: membershipEnabled && planId !== null,
    plan_id: planId
  },
  metadata: {
    session_id: sessionId,
    scheduled_time: scheduledTime,
    contact_id: input.contact_id || null,
    source: 'aigent_module_04'
  },
  trace_id: Date.now().toString()
};

// Check if customer exists, create if not
const lookupResult = $input.first().json;
const billingData = $('Prepare Billing Data').first().json;

let customerId;
const gateway = $env.PAYMENT_GATEWAY || 'stripe';

if (gateway === 'stripe') {
  // Stripe response format
  if (lookupResult.data && lookupResult.data.length > 0) {
    customerId = lookupResult.data[0].id;
  } else {
    // Will create new customer
    customerId = null;
  }
} else if (gateway === 'square') {
  // Square response format
  if (lookupResult.customers && lookupResult.customers.length > 0) {
    customerId = lookupResult.customers[0].id;
  } else {
    customerId = null;
  }
}

return {
  customer_exists: customerId !== null,
  customer_id: customerId,
  customer_email: billingData.customer.email,
  customer_name: billingData.customer.name,
  customer_phone: billingData.customer.phone,
  needs_creation: customerId === null
};

// Extract customer_id from either path
const checkResult = $('Check Customer Exists').first().json;
const createResult = $input.all().find(item => item.json.id || item.json.customer?.id);

let customerId;
const gateway = $env.PAYMENT_GATEWAY || 'stripe';

if (checkResult.customer_id) {
  // Existing customer
  customerId = checkResult.customer_id;
} else if (createResult) {
  // Newly created customer
  if (gateway === 'stripe') {
    customerId = createResult.json.id;
  } else if (gateway === 'square') {
    customerId = createResult.json.customer.id;
  }
}

const billingData = $('Prepare Billing Data').first().json;

return {
  customer_id: customerId,
  customer_email: billingData.customer.email,
  customer_name: billingData.customer.name,
  amount: billingData.billing.amount,
  currency: billingData.billing.currency,
  service_code: billingData.billing.service_code,
  service_description: billingData.billing.service_description,
  invoice_reference: billingData.billing.invoice_reference,
  membership: billingData.membership,
  metadata: billingData.metadata,
  trace_id: billingData.trace_id
};

// Format payment result for output and downstream nodes
const billingMode = $env.BILLING_MODE || 'invoice_link';
const gateway = $env.PAYMENT_GATEWAY || 'stripe';
const billingData = $('Extract Customer ID').first().json;

let invoiceId, paymentId, hostedInvoiceUrl, receiptUrl, paid;

if (billingMode === 'charge_now') {
  const chargeResult = $('Charge Invoice Now').first().json;

  if (gateway === 'stripe') {
    invoiceId = chargeResult.id;
    paymentId = chargeResult.charge || chargeResult.payment_intent;
    hostedInvoiceUrl = chargeResult.hosted_invoice_url;
    receiptUrl = chargeResult.charge_url || chargeResult.receipt_url;
    paid = chargeResult.status === 'paid';
  } else if (gateway === 'square') {
    invoiceId = chargeResult.invoice.id;
    paymentId = chargeResult.payment?.id || null;
    hostedInvoiceUrl = chargeResult.invoice.public_url;
    receiptUrl = chargeResult.payment?.receipt_url || null;
    paid = chargeResult.invoice.status === 'PAID';
  }
} else {
  // invoice_link mode
  const invoiceResult = $('Finalize Invoice (Stripe)').first().json;

  if (gateway === 'stripe') {
    invoiceId = invoiceResult.id;
    paymentId = null;
    hostedInvoiceUrl = invoiceResult.hosted_invoice_url;
    receiptUrl = null;
    paid = false;
  } else if (gateway === 'square') {
    const createResult = $('Create Invoice').first().json;
    invoiceId = createResult.invoice.id;
    paymentId = null;
    hostedInvoiceUrl = createResult.invoice.public_url;
    receiptUrl = null;
    paid = false;
  }
}

return {
  invoice_id: invoiceId,
  payment_id: paymentId,
  gateway: gateway.charAt(0).toUpperCase() + gateway.slice(1),
  amount: billingData.amount,
  currency: billingData.currency,
  paid: paid,
  customer_email: billingData.customer_email,
  customer_name: billingData.customer_name,
  customer_id: billingData.customer_id,
  hosted_invoice_url: hostedInvoiceUrl,
  receipt_url: receiptUrl,
  service_code: billingData.service_code,
  service_description: billingData.service_description,
  invoice_reference: billingData.invoice_reference,
  membership: billingData.membership,
  metadata: billingData.metadata,
  trace_id: billingData.trace_id,
  billing_mode: billingMode
};

