// Calculate reporting period based on REPORT_PERIOD_DAYS
const periodDays = parseInt($env.REPORT_PERIOD_DAYS || 30);
const endDate = new Date();
const startDate = new Date();
startDate.setDate(startDate.getDate() - periodDays);

const traceId = `ANALYTICS-${Date.now()}`;

return {
  json: {
    trace_id: traceId,
    period: {
      days: periodDays,
      start: startDate.toISOString(),
      end: endDate.toISOString(),
      label: `${startDate.toISOString().split('T')[0]} to ${endDate.toISOString().split('T')[0]}`
    },
    timestamp: new Date().toISOString(),
    data_sources: [],
    metrics: {}
  }
};

// Normalize leads data from multiple sources
const input = $input.all();
let leads = [];

input.forEach(item => {
  const source = item.json.source || 'unknown';

  // HubSpot format
  if (item.json.properties) {
    leads.push({
      email: item.json.properties.email,
      name: `${item.json.properties.firstname || ''} ${item.json.properties.lastname || ''}`.trim(),
      source: item.json.properties.lead_source || 'hubspot',
      created_at: item.json.properties.createdate,
      lifecycle_stage: item.json.properties.lifecyclestage
    });
  }
  // Google Sheets format
  else if (item.json.Email) {
    leads.push({
      email: item.json.Email,
      name: item.json.Name || item.json['Patient Name'],
      source: item.json.Source || 'google_sheets',
      created_at: item.json['Created At'] || item.json.Timestamp,
      lifecycle_stage: item.json.Stage
    });
  }
});

// Filter by date range
const startDate = new Date($node["Initialize Reporting Period"].json.period.start);
const endDate = new Date($node["Initialize Reporting Period"].json.period.end);

leads = leads.filter(lead => {
  if (!lead.created_at) return false;
  const leadDate = new Date(lead.created_at);
  return leadDate >= startDate && leadDate <= endDate;
});

return {
  json: {
    ...$node["Initialize Reporting Period"].json,
    leads: leads,
    data_sources: [...$node["Initialize Reporting Period"].json.data_sources, 'Leads']
  }
};

// Normalize bookings data
const input = $input.all();
let bookings = [];

input.forEach(item => {
  // Cal.com format
  if (item.json.bookings) {
    item.json.bookings.forEach(booking => {
      bookings.push({
        email: booking.attendees?.[0]?.email,
        patient_name: booking.attendees?.[0]?.name,
        appointment_date: booking.startTime,
        status: booking.status,
        visit_type: booking.eventType?.title,
        source: 'calcom'
      });
    });
  }
  // Google Sheets format
  else if (item.json['Appointment Date']) {
    bookings.push({
      email: item.json.Email || item.json['Patient Email'],
      patient_name: item.json['Patient Name'],
      appointment_date: item.json['Appointment Date'],
      status: item.json.Status,
      visit_type: item.json['Visit Type'],
      source: 'google_sheets'
    });
  }
});

// Filter by date range
const startDate = new Date($node["Initialize Reporting Period"].json.period.start);
const endDate = new Date($node["Initialize Reporting Period"].json.period.end);

bookings = bookings.filter(booking => {
  if (!booking.appointment_date) return false;
  const apptDate = new Date(booking.appointment_date);
  return apptDate >= startDate && apptDate <= endDate;
});

return {
  json: {
    ...$node["Normalize Leads Data"].json,
    bookings: bookings,
    data_sources: [...$node["Normalize Leads Data"].json.data_sources, 'Bookings']
  }
};

// Normalize payments data
const input = $input.all();
let payments = [];

input.forEach(item => {
  // Stripe format
  if (item.json.data) {
    item.json.data.forEach(charge => {
      payments.push({
        email: charge.billing_details?.email || charge.receipt_email,
        amount: charge.amount / 100,
        currency: charge.currency,
        status: charge.status,
        paid: charge.paid,
        created_at: new Date(charge.created * 1000).toISOString(),
        source: 'stripe'
      });
    });
  }
  // Google Sheets format
  else if (item.json.Amount) {
    payments.push({
      email: item.json.Email || item.json['Patient Email'],
      amount: parseFloat(item.json.Amount),
      currency: item.json.Currency || 'usd',
      status: item.json.Status,
      paid: item.json.Status === 'paid',
      created_at: item.json['Payment Date'] || item.json.Timestamp,
      source: 'google_sheets'
    });
  }
});

// Filter successful payments only
payments = payments.filter(p => p.paid && p.status === 'succeeded' || p.status === 'paid');

return {
  json: {
    ...$node["Normalize Bookings Data"].json,
    payments: payments,
    data_sources: [...$node["Normalize Bookings Data"].json.data_sources, 'Payments']
  }
};

// Normalize NPS data
const input = $input.all();
let npsScores = [];

input.forEach(item => {
  // Typeform format
  if (item.json.items) {
    item.json.items.forEach(response => {
      const npsAnswer = response.answers?.find(a => a.type === 'number' || a.type === 'opinion_scale');
      if (npsAnswer) {
        npsScores.push({
          email: response.hidden?.email,
          nps: npsAnswer.number || npsAnswer.opinion_scale,
          timestamp: response.submitted_at,
          source: 'typeform'
        });
      }
    });
  }
  // Google Sheets format
  else if (item.json['NPS Score']) {
    const nps = parseInt(item.json['NPS Score']);
    if (!isNaN(nps)) {
      npsScores.push({
        email: item.json.Email,
        nps: nps,
        timestamp: item.json.Timestamp,
        source: 'google_sheets'
      });
    }
  }
});

return {
  json: {
    ...$node["Normalize Payments Data"].json,
    nps_scores: npsScores,
    data_sources: [...$node["Normalize Payments Data"].json.data_sources, 'NPS']
  }
};

// Normalize OCR metrics
const input = $input.all();
let ocrMetrics = [];

input.forEach(item => {
  if (item.json.Timestamp && item.json['Document ID']) {
    ocrMetrics.push({
      document_id: item.json['Document ID'],
      doc_type: item.json['Doc Type'],
      confidence: parseFloat(item.json.Confidence) || 0,
      processing_time: parseFloat(item.json['Processing Time']) || null,
      timestamp: item.json.Timestamp,
      ocr_engine: item.json['OCR Engine']
    });
  }
});

// Filter by date range and confidence threshold
const startDate = new Date($node["Initialize Reporting Period"].json.period.start);
const endDate = new Date($node["Initialize Reporting Period"].json.period.end);
const minConfidence = parseFloat($env.MIN_CONFIDENCE_FILTER || 0.0);

ocrMetrics = ocrMetrics.filter(metric => {
  if (!metric.timestamp) return false;
  const metricDate = new Date(metric.timestamp);
  return metricDate >= startDate && metricDate <= endDate && metric.confidence >= minConfidence;
});

return {
  json: {
    ...$node["Normalize NPS Data"].json,
    ocr_metrics: ocrMetrics,
    data_sources: [...$node["Normalize NPS Data"].json.data_sources, 'OCR']
  }
};

// Compute KPIs from aggregated data
const data = $json;

const leads = data.leads || [];
const bookings = data.bookings || [];
const payments = data.payments || [];
const npsScores = data.nps_scores || [];
const ocrMetrics = data.ocr_metrics || [];

// Count unique completed visits (bookings with status = completed)
const completedVisits = bookings.filter(b => 
  b.status === 'completed' || b.status === 'attended'
).length;

// Revenue calculation
const totalRevenue = payments.reduce((sum, p) => sum + (p.amount || 0), 0);
const avgRevenuePerPatient = payments.length > 0 ? totalRevenue / payments.length : 0;

// NPS calculation
const npsAvg = npsScores.length > 0 
  ? npsScores.reduce((sum, s) => sum + s.nps, 0) / npsScores.length 
  : 0;

const promoters = npsScores.filter(s => s.nps >= 9).length;
const detractors = npsScores.filter(s => s.nps <= 6).length;
const npsScore = npsScores.length > 0 
  ? ((promoters - detractors) / npsScores.length) * 100 
  : 0;

// Conversion rates
const leadToBooking = leads.length > 0 ? bookings.length / leads.length : 0;
const bookingToVisit = bookings.length > 0 ? completedVisits / bookings.length : 0;
const visitToPayment = completedVisits > 0 ? payments.length / completedVisits : 0;

// No-show rate
const noShows = bookings.filter(b => b.status === 'no_show' || b.status === 'cancelled').length;
const showRate = bookings.length > 0 ? 1 - (noShows / bookings.length) : 0;

// Retention: patients with multiple visits
const patientEmails = new Set();
const repeatPatients = new Set();
bookings.forEach(b => {
  if (b.email) {
    if (patientEmails.has(b.email)) {
      repeatPatients.add(b.email);
    }
    patientEmails.add(b.email);
  }
});
const retentionRate = patientEmails.size > 0 ? repeatPatients.size / patientEmails.size : 0;

// OCR metrics
const avgOcrConfidence = ocrMetrics.length > 0
  ? ocrMetrics.reduce((sum, m) => sum + m.confidence, 0) / ocrMetrics.length
  : 0;

const avgProcessingTime = ocrMetrics.filter(m => m.processing_time).length > 0
  ? ocrMetrics.filter(m => m.processing_time).reduce((sum, m) => sum + m.processing_time, 0) 
    / ocrMetrics.filter(m => m.processing_time).length
  : 0;

return {
  json: {
    ...data,
    totals: {
      leads: leads.length,
      bookings: bookings.length,
      completed_visits: completedVisits,
      no_shows: noShows,
      revenue_usd: Math.round(totalRevenue * 100) / 100,
      payments_count: payments.length,
      nps_responses: npsScores.length,
      documents_processed: ocrMetrics.length
    },
    averages: {
      revenue_per_patient: Math.round(avgRevenuePerPatient * 100) / 100,
      nps_score: Math.round(npsAvg * 10) / 10,
      nps_net_score: Math.round(npsScore * 10) / 10,
      ocr_confidence: Math.round(avgOcrConfidence * 100) / 100,
      doc_processing_time_sec: Math.round(avgProcessingTime * 10) / 10
    },
    rates: {
      lead_to_booking: Math.round(leadToBooking * 100) / 100,
      booking_to_visit: Math.round(bookingToVisit * 100) / 100,
      visit_to_payment: Math.round(visitToPayment * 100) / 100,
      show_rate: Math.round(showRate * 100) / 100,
      retention_rate: Math.round(retentionRate * 100) / 100
    },
    segments: {
      promoters: promoters,
      passives: npsScores.filter(s => s.nps >= 7 && s.nps <= 8).length,
      detractors: detractors,
      unique_patients: patientEmails.size,
      repeat_patients: repeatPatients.size
    }
  }
};

// Generate report URL based on storage provider
const storageProvider = $env.STORAGE_PROVIDER || 's3';
const fileName = `${$env.BRAND_NAME || 'clinic'}_report_${new Date().toISOString().split('T')[0]}.html`;

let reportUrl = '';

if (storageProvider === 's3') {
  const bucket = $env.S3_BUCKET || 'clinic-reports';
  const region = $env.S3_REGION || 'us-east-1';
  reportUrl = `https://${bucket}.s3.${region}.amazonaws.com/${fileName}`;
}
else if (storageProvider === 'gdrive') {
  // Google Drive file ID from upload response
  const fileId = $json.id || 'unknown';
  reportUrl = `https://drive.google.com/file/d/${fileId}/view`;
}

return {
  json: {
    ...$json,
    report_url: reportUrl
  }
};

// Build final aggregated_metrics.json output
const data = $json;

const output = {
  success: true,
  trace_id: data.trace_id,
  period: data.period.label,
  totals: {
    leads: data.totals.leads,
    bookings: data.totals.bookings,
    completed_visits: data.totals.completed_visits,
    revenue_usd: data.totals.revenue_usd,
    nps_avg: data.averages.nps_score,
    doc_processing_time_avg_sec: data.averages.doc_processing_time_sec
  },
  rates: {
    lead_to_booking: data.rates.lead_to_booking,
    booking_to_visit: data.rates.booking_to_visit,
    visit_to_payment: data.rates.visit_to_payment,
    show_rate: data.rates.show_rate,
    retention_rate: data.rates.retention_rate
  },
  report_links: {
    html_report: data.report_url || null,
    sheet_url: `https://docs.google.com/spreadsheets/d/${$env.GOOGLE_SHEET_ID}`,
    looker_dashboard: $env.LOOKER_STUDIO_URL || null
  },
  metadata: {
    generated_at: new Date().toISOString(),
    data_sources: data.data_sources,
    processed_by: 'Aigent_Module_07'
  }
};

return { json: output };

// Error handler
const error = $input.item.json.error || 'Unknown error occurred';
const stage = $input.item.json.stage || 'unknown';
const traceId = $input.item.json.trace_id || `ERR-${Date.now()}`;

const errorResponse = {
  success: false,
  error: error.message || error,
  stage: stage,
  trace_id: traceId,
  timestamp: new Date().toISOString()
};

if ($env.DEBUG_MODE === 'true') {
  console.error('Module 07 Error:', JSON.stringify(errorResponse, null, 2));
}

return { json: errorResponse };
