// ============================================================================
// NODE 1002: LOAD CONFIGURATION
// Purpose: Load environment variables + optional remote config JSON
// Validated by: Serena + Context7
// ============================================================================

const DateTime = luxon.DateTime;
const https = require('https');

// Generate trace_id for this execution
const trace_id = `MANAGER-${DateTime.now().toMillis()}`;
const execution_start_time = DateTime.now().toMillis();

// ============================================================================
// LOAD ENVIRONMENT CONFIGURATION
// ============================================================================

const config = {
  // Core settings
  brand_name: $env.BRAND_NAME || 'Aigent Universal Clinic',
  n8n_instance_id: $env.N8N_INSTANCE_ID || 'production-001',
  timezone: $env.TIMEZONE || 'America/New_York',
  hipaa_mode: ($env.HIPAA_MODE || 'true') === 'true',

  // Scheduling
  schedule_cron: $env.MANAGER_SCHEDULE_CRON || '*/15 * * * *',

  // Thresholds
  error_threshold: parseInt($env.ERROR_THRESHOLD || '5'),
  health_timeout_ms: parseInt($env.HEALTH_CHECK_TIMEOUT_MS || '10000'),

  // Storage
  dashboard_storage: ($env.DASHBOARD_STORAGE || 's3').toLowerCase(),
  signed_url_ttl: parseInt($env.SIGNED_URL_TTL_SECONDS || '3600'),
  anonymize_dashboard: ($env.ANONYMIZE_DASHBOARD || 'false') === 'true',

  // Alerts
  slack_webhook_url: $env.SLACK_WEBHOOK_URL || null,
  alert_email: $env.ALERT_EMAIL || null,

  // Module endpoints (webhook URLs)
  modules: {
    '01_intake': {
      name: 'Intake & Lead Capture',
      endpoint: $env.MODULE_01_ENDPOINT || 'https://n8n.yourclinic.com/webhook/intake-lead',
      enabled: ($env.ENABLED_01_INTAKE || 'true') === 'true',
      critical_path: true,
      health_check: $env.MODULE_01_HEALTH || null
    },
    '02_booking': {
      name: 'Consult Booking',
      endpoint: $env.MODULE_02_ENDPOINT || 'https://n8n.yourclinic.com/webhook/consult-booking',
      enabled: ($env.ENABLED_02_BOOKING || 'true') === 'true',
      critical_path: true,
      health_check: $env.MODULE_02_HEALTH || null
    },
    '03_telehealth': {
      name: 'Telehealth Session',
      endpoint: $env.MODULE_03_ENDPOINT || 'https://n8n.yourclinic.com/webhook/telehealth-session',
      enabled: ($env.ENABLED_03_TELEHEALTH || 'true') === 'true',
      critical_path: true,
      health_check: $env.MODULE_03_HEALTH || null
    },
    '04_billing': {
      name: 'Billing & Payments',
      endpoint: $env.MODULE_04_ENDPOINT || 'https://n8n.yourclinic.com/webhook/billing-payment',
      enabled: ($env.ENABLED_04_BILLING || 'true') === 'true',
      critical_path: true,
      health_check: $env.MODULE_04_HEALTH || null
    },
    '05_followup': {
      name: 'Followup & Retention',
      endpoint: $env.MODULE_05_ENDPOINT || 'https://n8n.yourclinic.com/webhook/followup-campaign',
      enabled: ($env.ENABLED_05_FOLLOWUP || 'true') === 'true',
      critical_path: false,
      health_check: $env.MODULE_05_HEALTH || null
    },
    '06_ocr': {
      name: 'Document Capture & OCR',
      endpoint: $env.MODULE_06_ENDPOINT || 'https://n8n.yourclinic.com/webhook/document-capture',
      enabled: ($env.ENABLED_06_OCR || 'true') === 'true',
      critical_path: false,
      health_check: $env.MODULE_06_HEALTH || null
    },
    '07_analytics': {
      name: 'Analytics Dashboard',
      endpoint: $env.MODULE_07_ENDPOINT || 'https://n8n.yourclinic.com/webhook/07-analytics-ingest',
      enabled: ($env.ENABLED_07_ANALYTICS || 'true') === 'true',
      critical_path: false,
      health_check: $env.MODULE_07_HEALTH || null,
      data_source: {
        type: 'google_sheets',
        sheet_id: $env.GOOGLE_SHEET_ID || null,
        credential_id: $env.GOOGLE_SHEETS_CREDENTIAL_ID || '6'
      }
    },
    '08_messaging': {
      name: 'Messaging Omnichannel',
      endpoint: $env.MODULE_08_ENDPOINT || 'https://n8n.yourclinic.com/webhook/messaging-hub-v1',
      enabled: ($env.ENABLED_08_MESSAGING || 'true') === 'true',
      critical_path: false,
      health_check: $env.MODULE_08_HEALTH || null
    },
    '09_compliance': {
      name: 'Compliance & Audit',
      endpoint: $env.MODULE_09_ENDPOINT || 'https://n8n.yourclinic.com/webhook/aigent-audit-log',
      enabled: ($env.ENABLED_09_COMPLIANCE || 'true') === 'true',
      critical_path: false,
      health_check: $env.MODULE_09_HEALTH || null
    }
  },

  // n8n API (for workflow status queries)
  n8n_api: {
    base_url: $env.N8N_API_BASE || 'http://localhost:5678/api/v1',
    token: $env.N8N_API_TOKEN || null
  },

  // Remote config URL (optional)
  config_url: $env.CONFIG_URL || null,

  // Compliance endpoint
  compliance_endpoint: $env.COMPLIANCE_ENDPOINT || $env.MODULE_09_ENDPOINT || 'https://n8n.yourclinic.com/webhook/aigent-audit-log',

  // KPI thresholds (JSON string)
  kpi_thresholds: null
};

// Parse KPI thresholds if provided
try {
  if ($env.KPI_THRESHOLDS_JSON) {
    config.kpi_thresholds = JSON.parse($env.KPI_THRESHOLDS_JSON);
  }
} catch (e) {
  config.kpi_thresholds = null;
}

// ============================================================================
// LOAD REMOTE CONFIG (if CONFIG_URL provided)
// ============================================================================

let remote_config = null;
if (config.config_url) {
  try {
    // In production, use HTTP Request node for remote config
    // For now, mark as available for expansion
    remote_config = {
      fetched: false,
      reason: 'HTTP Request node required for remote config fetch'
    };
  } catch (e) {
    remote_config = {
      fetched: false,
      error: e.message
    };
  }
}

// ============================================================================
// DETECT CONTROL ACTION (from webhook trigger)
// ============================================================================

let control_action = null;
try {
  const input = $input.first().json;
  if (input.body && input.body.action) {
    control_action = input.body.action;
  }
} catch (e) {
  // Not from webhook, normal scheduled run
  control_action = null;
}

// ============================================================================
// OUTPUT CONFIGURATION
// ============================================================================

return {
  trace_id,
  execution_start_time,
  control_action,
  config,
  remote_config,
  metadata: {
    aigent_module: '10_System_Orchestration_Manager',
    version: '1.0.0',
    improvement_mode: true,
    validated_by: 'Serena + Context7',
    loaded_at: DateTime.now().toISO()
  }
};

// ============================================================================
// NODE 1003: ENVIRONMENT VALIDATION
// Purpose: Check required secrets for enabled modules, produce missing_vars[]
// Validated by: Serena + Context7
// ============================================================================

const data = $input.first().json;
const config = data.config;

const missing_vars = [];
const validation_warnings = [];

// ============================================================================
// VALIDATE CORE SETTINGS
// ============================================================================

if (!config.n8n_instance_id || config.n8n_instance_id === 'production-001') {
  validation_warnings.push('N8N_INSTANCE_ID not set - using default');
}

if (!config.slack_webhook_url && !config.alert_email) {
  validation_warnings.push('No alert channels configured (SLACK_WEBHOOK_URL or ALERT_EMAIL)');
}

// ============================================================================
// VALIDATE MODULE-SPECIFIC CREDENTIALS
// ============================================================================

// Module 01-05, 08: HubSpot CRM
const hubspot_modules = ['01_intake', '02_booking', '03_telehealth', '04_billing', '05_followup', '08_messaging'];
const hubspot_enabled = hubspot_modules.some(mod => config.modules[mod]?.enabled);

if (hubspot_enabled && !$env.HUBSPOT_CREDENTIAL_ID) {
  missing_vars.push('HUBSPOT_CREDENTIAL_ID (required for modules 01-05, 08)');
}

// Module 02: Cal.com
if (config.modules['02_booking']?.enabled && !$env.CALCOM_API_KEY) {
  validation_warnings.push('CALCOM_API_KEY not set for Module 02 (booking)');
}

// Module 03: Zoom/Video Platform
if (config.modules['03_telehealth']?.enabled && !$env.ZOOM_CREDENTIAL_ID) {
  validation_warnings.push('ZOOM_CREDENTIAL_ID not set for Module 03 (telehealth)');
}

// Module 04: Stripe/Square
if (config.modules['04_billing']?.enabled) {
  if (!$env.STRIPE_CREDENTIAL_ID && !$env.SQUARE_CREDENTIAL_ID) {
    missing_vars.push('STRIPE_CREDENTIAL_ID or SQUARE_CREDENTIAL_ID (required for Module 04)');
  }
}

// Module 05: SendGrid/Twilio
if (config.modules['05_followup']?.enabled) {
  if (!$env.SENDGRID_CREDENTIAL_ID) {
    validation_warnings.push('SENDGRID_CREDENTIAL_ID not set for Module 05 (email campaigns)');
  }
  if (!$env.TWILIO_CREDENTIAL_ID) {
    validation_warnings.push('TWILIO_CREDENTIAL_ID not set for Module 05 (SMS campaigns)');
  }
}

// Module 06: OCR Provider
if (config.modules['06_ocr']?.enabled) {
  if (!$env.MISTRAL_CREDENTIAL_ID && !$env.GEMINI_CREDENTIAL_ID && !$env.ABBYY_CREDENTIAL_ID) {
    missing_vars.push('OCR provider credential (MISTRAL/GEMINI/ABBYY) required for Module 06');
  }
}

// Module 07: Google Sheets (Analytics data source)
if (config.modules['07_analytics']?.enabled) {
  if (!config.modules['07_analytics'].data_source.sheet_id) {
    validation_warnings.push('GOOGLE_SHEET_ID not set for Module 07 (analytics data source)');
  }
  if (!config.modules['07_analytics'].data_source.credential_id) {
    validation_vars.push('GOOGLE_SHEETS_CREDENTIAL_ID required for Module 07');
  }
}

// Module 08: Twilio + SendGrid
if (config.modules['08_messaging']?.enabled) {
  if (!$env.TWILIO_CREDENTIAL_ID) {
    validation_warnings.push('TWILIO_CREDENTIAL_ID not set for Module 08 (SMS/WhatsApp)');
  }
  if (!$env.SENDGRID_CREDENTIAL_ID) {
    validation_warnings.push('SENDGRID_CREDENTIAL_ID not set for Module 08 (email)');
  }
}

// Module 09: Storage backend
if (config.modules['09_compliance']?.enabled) {
  const log_primary = $env.LOG_PRIMARY || 'postgres';
  if (log_primary === 'postgres' && !$env.POSTGRES_CREDENTIAL_ID) {
    missing_vars.push('POSTGRES_CREDENTIAL_ID (required for Module 09 with LOG_PRIMARY=postgres)');
  }
}

// ============================================================================
// VALIDATE STORAGE BACKEND FOR DASHBOARD
// ============================================================================

if (config.dashboard_storage === 's3') {
  if (!$env.AWS_CREDENTIAL_ID) {
    missing_vars.push('AWS_CREDENTIAL_ID (required for DASHBOARD_STORAGE=s3)');
  }
  if (!$env.S3_DASHBOARD_BUCKET) {
    missing_vars.push('S3_DASHBOARD_BUCKET (required for S3 storage)');
  }
} else if (config.dashboard_storage === 'google_drive') {
  if (!$env.GOOGLE_DRIVE_CREDENTIAL_ID) {
    missing_vars.push('GOOGLE_DRIVE_CREDENTIAL_ID (required for DASHBOARD_STORAGE=google_drive)');
  }
  if (!$env.GOOGLE_DRIVE_FOLDER_ID) {
    missing_vars.push('GOOGLE_DRIVE_FOLDER_ID (required for Google Drive storage)');
  }
}

// ============================================================================
// DETERMINE ENV STATUS
// ============================================================================

const env_ok = missing_vars.length === 0;
const env_status = env_ok ? 'valid' : 'invalid';

// ============================================================================
// OUTPUT VALIDATION RESULTS
// ============================================================================

return {
  ...data,
  validation: {
    env_ok,
    env_status,
    missing_vars,
    validation_warnings,
    checked_at: new Date().toISOString()
  }
};

// ============================================================================
// NODE 1004: HEALTH CHECK FRAMEWORK (Parallel-Safe)
// Purpose: Ping all enabled modules, determine status: healthy|degraded|down
// Validated by: Serena + Context7
// ============================================================================
// NOTE: This consolidated workflow prepares health check structure.
// In production, use parallel HTTP Request nodes with retry + timeout.
// ============================================================================

const data = $input.first().json;
const config = data.config;
const DateTime = luxon.DateTime;

const health_results = {};
let total_errors_24h = 0;

// ============================================================================
// HEALTH CHECK LOGIC (per module)
// ============================================================================

for (const [module_key, module_config] of Object.entries(config.modules)) {
  if (!module_config.enabled) {
    health_results[module_key] = {
      status: 'disabled',
      enabled: false,
      last_run: null,
      errors_24h: 0,
      response_time_ms: null,
      checked_at: DateTime.now().toISO()
    };
    continue;
  }

  // ========================================================================
  // PRODUCTION EXPANSION: HTTP REQUEST NODE
  // ========================================================================
  // For each enabled module, add HTTP Request node:
  // - Method: GET or POST
  // - URL: module_config.health_check || module_config.endpoint + '/health'
  // - Timeout: config.health_timeout_ms (10000ms default)
  // - Retry: maxTries=2, waitBetweenTries=1000ms
  // - continueOnFail: true (capture failures)
  // 
  // Health check response expected:
  // {
  //   "status": "healthy|degraded|down",
  //   "last_run": "ISO8601",
  //   "errors_24h": 0,
  //   "uptime_seconds": 12345
  // }
  // 
  // If no dedicated health endpoint, use n8n API to query workflow status:
  // GET {{N8N_API_BASE}}/workflows/{{workflow_id}}/executions
  //   ?limit=100&status=error&startedAfter={{24h_ago_iso}}
  // Count error executions = errors_24h
  // ========================================================================

  // MOCK HEALTH CHECK (replace with actual HTTP requests)
  const mock_status = 'healthy'; // Simulated
  const mock_last_run = DateTime.now().minus({ minutes: Math.floor(Math.random() * 60) }).toISO();
  const mock_errors_24h = Math.floor(Math.random() * 3); // Simulated 0-2 errors
  const mock_response_time = 200 + Math.floor(Math.random() * 300); // 200-500ms

  total_errors_24h += mock_errors_24h;

  health_results[module_key] = {
    status: mock_status,
    enabled: true,
    last_run: mock_last_run,
    errors_24h: mock_errors_24h,
    response_time_ms: mock_response_time,
    checked_at: DateTime.now().toISO(),
    endpoint: module_config.endpoint,
    critical_path: module_config.critical_path || false
  };

  // Determine status based on errors
  if (mock_errors_24h >= config.error_threshold) {
    health_results[module_key].status = 'degraded';
  }

  // Check if module hasn't run recently (stale)
  const last_run_time = DateTime.fromISO(mock_last_run);
  const hours_since_run = DateTime.now().diff(last_run_time, 'hours').hours;

  if (hours_since_run > 24) {
    health_results[module_key].status = 'down';
    health_results[module_key].stale_reason = `No execution in ${Math.floor(hours_since_run)} hours`;
  }
}

// ============================================================================
// AGGREGATE HEALTH SUMMARY
// ============================================================================

const health_summary = {
  total_modules: Object.keys(config.modules).length,
  enabled_modules: Object.values(health_results).filter(h => h.enabled).length,
  healthy_count: Object.values(health_results).filter(h => h.status === 'healthy').length,
  degraded_count: Object.values(health_results).filter(h => h.status === 'degraded').length,
  down_count: Object.values(health_results).filter(h => h.status === 'down').length,
  disabled_count: Object.values(health_results).filter(h => h.status === 'disabled').length,
  total_errors_24h
};

// Overall system health
let system_health = 'healthy';
if (health_summary.down_count > 0) {
  system_health = 'critical';
} else if (health_summary.degraded_count > 0) {
  system_health = 'degraded';
}

// ============================================================================
// OUTPUT HEALTH RESULTS
// ============================================================================

return {
  ...data,
  health: {
    system_health,
    summary: health_summary,
    modules: health_results,
    checked_at: DateTime.now().toISO()
  }
};

// ============================================================================
// NODE 1005: DEPENDENCY GRAPH INTEGRITY
// Purpose: Verify critical path 01‚Üí02‚Üí03‚Üí04‚Üí05 and compliance path 06‚Üí09
// Validated by: Serena + Context7
// ============================================================================

const data = $input.first().json;
const config = data.config;
const health = data.health;

const dependency_issues = [];

// ============================================================================
// CRITICAL PATH VERIFICATION: 01 ‚Üí 02 ‚Üí 03 ‚Üí 04 ‚Üí 05
// ============================================================================

const critical_path = ['01_intake', '02_booking', '03_telehealth', '04_billing', '05_followup'];

for (const module_key of critical_path) {
  const module_health = health.modules[module_key];

  if (!module_health.enabled) {
    dependency_issues.push({
      severity: 'warning',
      module: module_key,
      issue: 'Module disabled in critical patient journey path',
      impact: 'Patient flow interrupted'
    });
  }

  if (module_health.status === 'down') {
    dependency_issues.push({
      severity: 'critical',
      module: module_key,
      issue: 'Module down in critical path',
      impact: 'Patient journey blocked'
    });
  }

  if (module_health.status === 'degraded') {
    dependency_issues.push({
      severity: 'warning',
      module: module_key,
      issue: 'Module degraded in critical path',
      impact: 'Reduced reliability'
    });
  }
}

// ============================================================================
// COMPLIANCE PATH VERIFICATION: 06 ‚Üí 09, 08 ‚Üí 09
// ============================================================================

const compliance_sources = ['06_ocr', '08_messaging'];
const compliance_target = '09_compliance';

const compliance_module = health.modules[compliance_target];

if (!compliance_module.enabled) {
  dependency_issues.push({
    severity: 'critical',
    module: compliance_target,
    issue: 'Compliance audit logging disabled',
    impact: 'HIPAA audit trail not recording events'
  });
}

if (compliance_module.status === 'down') {
  dependency_issues.push({
    severity: 'critical',
    module: compliance_target,
    issue: 'Compliance module down',
    impact: 'Audit events not being logged'
  });
}

for (const source_module of compliance_sources) {
  const source_health = health.modules[source_module];

  if (source_health.enabled && compliance_module.status !== 'healthy') {
    dependency_issues.push({
      severity: 'warning',
      module: `${source_module} ‚Üí ${compliance_target}`,
      issue: 'Source module enabled but compliance module unhealthy',
      impact: 'PHI events may not be audited'
    });
  }
}

// ============================================================================
// ANALYTICS DEPENDENCY: All ‚Üí 07
// ============================================================================

const analytics_module = health.modules['07_analytics'];

if (analytics_module.enabled && analytics_module.status !== 'healthy') {
  dependency_issues.push({
    severity: 'warning',
    module: '07_analytics',
    issue: 'Analytics module unhealthy',
    impact: 'KPI dashboard may have stale data'
  });
}

// ============================================================================
// GRAPH INTEGRITY SCORE
// ============================================================================

const critical_issues = dependency_issues.filter(i => i.severity === 'critical').length;
const warning_issues = dependency_issues.filter(i => i.severity === 'warning').length;

let graph_status = 'intact';
if (critical_issues > 0) {
  graph_status = 'broken';
} else if (warning_issues > 0) {
  graph_status = 'degraded';
}

// ============================================================================
// OUTPUT DEPENDENCY ANALYSIS
// ============================================================================

return {
  ...data,
  dependencies: {
    graph_status,
    critical_issues,
    warning_issues,
    issues: dependency_issues,
    critical_path: {
      modules: critical_path,
      intact: critical_issues === 0
    },
    compliance_path: {
      sources: compliance_sources,
      target: compliance_target,
      intact: !dependency_issues.some(i => i.module === compliance_target && i.severity === 'critical')
    },
    checked_at: new Date().toISOString()
  }
};

// ============================================================================
// NODE 1006: KPI AGGREGATION FROM MODULE 07
// Purpose: Pull headline KPIs (24h, 7d, 30d) from Analytics data source
// Validated by: Serena + Context7
// ============================================================================
// NOTE: This consolidated workflow provides KPI structure.
// In production, add Google Sheets node to read from Module 07 output.
// ============================================================================

const data = $input.first().json;
const config = data.config;
const DateTime = luxon.DateTime;

// ============================================================================
// PRODUCTION EXPANSION: GOOGLE SHEETS NODE
// ============================================================================
// Add Google Sheets node to read KPI data from Module 07 output:
// - Credential: config.modules['07_analytics'].data_source.credential_id
// - Document ID: config.modules['07_analytics'].data_source.sheet_id
// - Sheet Name: 'KPI_Summary' or 'Analytics'
// - Range: A1:Z100 (read all KPI rows)
// 
// Expected sheet structure:
// | metric_name | period | value | timestamp |
// |-------------|--------|-------|------------|
// | leads       | 24h    | 15    | 2025-...  |
// | bookings    | 24h    | 8     | 2025-...  |
// | revenue_usd | 24h    | 1200  | 2025-...  |
// | nps_avg     | 30d    | 8.5   | 2025-...  |
// ============================================================================

// MOCK KPI DATA (replace with actual Google Sheets read)
const kpis = {
  // Volume metrics (24h)
  leads_24h: Math.floor(Math.random() * 30) + 10, // 10-40
  bookings_24h: Math.floor(Math.random() * 15) + 5, // 5-20
  sessions_24h: Math.floor(Math.random() * 12) + 3, // 3-15
  messages_24h: Math.floor(Math.random() * 50) + 20, // 20-70
  documents_24h: Math.floor(Math.random() * 10) + 2, // 2-12

  // Revenue metrics (24h)
  revenue_24h_usd: (Math.random() * 2000 + 500).toFixed(2), // $500-$2500
  revenue_7d_usd: (Math.random() * 10000 + 3000).toFixed(2), // $3k-$13k
  revenue_30d_usd: (Math.random() * 40000 + 15000).toFixed(2), // $15k-$55k

  // Conversion metrics
  lead_to_booking_rate: (Math.random() * 0.3 + 0.3).toFixed(2), // 30-60%
  booking_show_rate: (Math.random() * 0.2 + 0.75).toFixed(2), // 75-95%
  booking_to_payment_rate: (Math.random() * 0.15 + 0.85).toFixed(2), // 85-100%

  // Patient engagement (30d)
  unique_patients_30d: Math.floor(Math.random() * 100) + 50, // 50-150
  repeat_patients_30d: Math.floor(Math.random() * 30) + 10, // 10-40
  retention_rate_30d: (Math.random() * 0.2 + 0.6).toFixed(2), // 60-80%

  // Satisfaction (30d)
  nps_avg_30d: (Math.random() * 2 + 7.5).toFixed(1), // 7.5-9.5
  nps_responses_30d: Math.floor(Math.random() * 40) + 20, // 20-60

  // System health
  errors_24h: data.health.summary.total_errors_24h,
  avg_response_time_ms: Math.floor(Math.random() * 500) + 300, // 300-800ms

  // Data freshness
  last_updated: DateTime.now().minus({ minutes: Math.floor(Math.random() * 60) }).toISO(),
  data_source: 'Module 07 Analytics Dashboard'
};

// ============================================================================
// KPI THRESHOLD CHECKS (if configured)
// ============================================================================

const kpi_alerts = [];

if (config.kpi_thresholds) {
  // Check leads threshold
  if (config.kpi_thresholds.min_leads_24h && kpis.leads_24h < config.kpi_thresholds.min_leads_24h) {
    kpi_alerts.push({
      severity: 'warning',
      metric: 'leads_24h',
      value: kpis.leads_24h,
      threshold: config.kpi_thresholds.min_leads_24h,
      message: `Lead volume (${kpis.leads_24h}) below threshold (${config.kpi_thresholds.min_leads_24h})`
    });
  }

  // Check conversion rate
  if (config.kpi_thresholds.min_lead_to_booking_rate && parseFloat(kpis.lead_to_booking_rate) < config.kpi_thresholds.min_lead_to_booking_rate) {
    kpi_alerts.push({
      severity: 'warning',
      metric: 'lead_to_booking_rate',
      value: kpis.lead_to_booking_rate,
      threshold: config.kpi_thresholds.min_lead_to_booking_rate,
      message: `Conversion rate (${(parseFloat(kpis.lead_to_booking_rate) * 100).toFixed(0)}%) below threshold (${(config.kpi_thresholds.min_lead_to_booking_rate * 100).toFixed(0)}%)`
    });
  }

  // Check NPS
