  if (module_health.status === 'degraded') {
    all_alerts.push({
      severity: 'warning',
      category: 'health',
      module: module_key,
      title: `Module Degraded: ${config.modules[module_key].name}`,
      message: `${module_health.errors_24h} errors in last 24 hours (threshold: ${config.error_threshold})`,
      action: 'Review error logs and investigate root cause'
    });
  }
}

