// NODE 1013: ERROR HANDLER
// Purpose: Catch errors, post to Compliance (Module 09), return error response
// Validated by: Serena + Context7
// ============================================================================

const DateTime = luxon.DateTime;

// Get error details
const error = $input.first().error || {};
const errorMessage = error.message || 'Unknown error in Manager orchestration';
const errorStack = error.stack || '';

// Try to get trace_id from previous execution
let trace_id = 'MANAGER-ERROR-' + DateTime.now().toMillis();
let stage = 'unknown';
try {
  const prevData = $('1002: Load Configuration').first().json;
  if (prevData && prevData.trace_id) {
    trace_id = prevData.trace_id;
  }
} catch (e) {
  // Could not retrieve previous data
}

// Determine stage from error context
if (errorMessage.includes('environment') || errorMessage.includes('validation')) {
  stage = 'env_validation';
} else if (errorMessage.includes('health') || errorMessage.includes('ping')) {
  stage = 'health_check';
} else if (errorMessage.includes('dependency') || errorMessage.includes('graph')) {
  stage = 'dependency_check';
} else if (errorMessage.includes('KPI') || errorMessage.includes('analytics')) {
  stage = 'kpi_aggregation';
} else if (errorMessage.includes('dashboard') || errorMessage.includes('render')) {
  stage = 'dashboard_render';
} else if (errorMessage.includes('publish') || errorMessage.includes('storage')) {
  stage = 'dashboard_publish';
} else if (errorMessage.includes('alert') || errorMessage.includes('notify')) {
  stage = 'alert_notification';
}

