// BUILD ERROR RESPONSE
// ============================================================================

const error_response = {
  success: false,
  error: errorMessage,
  error_type: 'OrchestrationError',
  stage: stage,
  trace_id: trace_id,
  timestamp: DateTime.now().toISO(),
  metadata: {
    module: '10_System_Orchestration_Manager',
    version: '1.0.0',
