const operation = $input.item.json.params?.operation || 'mock-fetch';
const body = $input.item.json.body || {};
const trace_id = `MOCK-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`;

return {
  operation: operation,
  body: body,
  trace_id: trace_id,
  timestamp: new Date().toISOString()
};

const fs = require('fs');
const connectorId = $input.item.json.body.connector_id;
const endpoint = $input.item.json.body.endpoint;
const mockDataInput = $input.item.json.body.mock_data;

const mockBasePath = process.env.MOCK_BASE_PATH || '/data/Aigent_Modules_Core/mocks';

let mockPath;
let attemptedPath;

if (mockDataInput) {
  if (typeof mockDataInput === 'object' && mockDataInput !== null && !Array.isArray(mockDataInput)) {
    const endpointPath = mockDataInput[endpoint];
    if (endpointPath) {
      if (endpointPath.startsWith('/')) {
        mockPath = endpointPath;
      } else {
        mockPath = `${mockBasePath}/${endpointPath}`;
      }
      attemptedPath = mockPath;
    } else {
      mockPath = `${mockBasePath}/${connectorId}_${endpoint}.json`;
      attemptedPath = mockPath;
    }
  } else if (typeof mockDataInput === 'string') {
    if (mockDataInput.startsWith('/')) {
      mockPath = mockDataInput;
    } else {
      mockPath = `${mockBasePath}/${mockDataInput}`;
    }
    attemptedPath = mockPath;
  } else {
    mockPath = `${mockBasePath}/${connectorId}_${endpoint}.json`;
    attemptedPath = mockPath;
  }
} else {
  mockPath = `${mockBasePath}/${connectorId}_${endpoint}.json`;
  attemptedPath = mockPath;
}

try {
  const mockData = fs.readFileSync(mockPath, 'utf8');
  const mockResponse = JSON.parse(mockData);

  const latencyConfig = process.env.MOCK_LATENCY_MS || '0';
  const [minLatency, maxLatency] = latencyConfig.includes('-') 
    ? latencyConfig.split('-').map(Number)
    : [Number(latencyConfig), Number(latencyConfig)];

  const simulatedLatency = minLatency + Math.random() * (maxLatency - minLatency);

  const errorRate = parseFloat(process.env.MOCK_RANDOM_ERROR_RATE || '0');
  const shouldError = Math.random() < errorRate;

  if (shouldError) {
    return {
      success: false,
      source: 'mock',
      error: 'mock_file_error',
      error_message: 'Random error injection triggered',
      connector_id: connectorId,
      endpoint: endpoint,
      attempted_path: attemptedPath,
      trace_id: $input.item.json.trace_id,
      simulated_latency_ms: Math.round(simulatedLatency)
    };
  }

  return {
    success: true,
    source: 'mock',
    connector_id: connectorId,
    endpoint: endpoint,
    data: mockResponse,
    mock_file: mockPath,
    simulated_latency_ms: Math.round(simulatedLatency),
    trace_id: $input.item.json.trace_id
  };
} catch (error) {
  return {
    success: false,
    source: 'mock',
    error: 'mock_file_error',
    error_message: error.message,
    connector_id: connectorId,
    endpoint: endpoint,
    attempted_path: attemptedPath,
    trace_id: $input.item.json.trace_id
  };
}

const fs = require('fs');
const path = require('path');

const connectorId = $input.item.json.body.connector_id;
const endpoint = $input.item.json.body.endpoint;
const mockData = $input.item.json.body.mock_data;

const mockBasePath = process.env.MOCK_BASE_PATH || '/data/Aigent_Modules_Core/mocks';

if (!fs.existsSync(mockBasePath)) {
  fs.mkdirSync(mockBasePath, { recursive: true });
}

const filename = `${connectorId}_${endpoint}.json`;
const mockPath = path.join(mockBasePath, filename);

try {
  const dataToWrite = typeof mockData === 'string' 
    ? mockData 
    : JSON.stringify(mockData, null, 2);

  fs.writeFileSync(mockPath, dataToWrite, 'utf8');

  const indexPath = path.join(mockBasePath, 'index.json');
  let index = [];
  if (fs.existsSync(indexPath)) {
    index = JSON.parse(fs.readFileSync(indexPath, 'utf8'));
  }

  const existing = index.find(i => i.connector_id === connectorId && i.endpoint === endpoint);
  if (existing) {
    existing.updated_at = new Date().toISOString();
  } else {
    index.push({
      connector_id: connectorId,
      endpoint: endpoint,
      filename: filename,
      created_at: new Date().toISOString()
    });
  }

  fs.writeFileSync(indexPath, JSON.stringify(index, null, 2), 'utf8');

  return {
    success: true,
    connector_id: connectorId,
    endpoint: endpoint,
    filename: filename,
    path: mockPath,
    trace_id: $input.item.json.trace_id
  };
} catch (error) {
  return {
    success: false,
    error: 'Failed to upload mock',
    error_message: error.message,
    trace_id: $input.item.json.trace_id
  };
}

const fs = require('fs');
const path = require('path');

const connectorId = $input.item.json.body.connector_id;
const endpoint = $input.item.json.body.endpoint;

const mockBasePath = process.env.MOCK_BASE_PATH || '/data/Aigent_Modules_Core/mocks';
const schemaBasePath = process.env.SCHEMA_BASE_PATH || '/data/Aigent_Modules_Core/schemas';

const mockPath = path.join(mockBasePath, `${connectorId}_${endpoint}.json`);
if (!fs.existsSync(mockPath)) {
  return {
    success: false,
    error: 'Mock file not found',
    path: mockPath,
    trace_id: $input.item.json.trace_id
  };
}

const mockData = JSON.parse(fs.readFileSync(mockPath, 'utf8'));

const registry = globalThis.aigent_registry || [];
if (!Array.isArray(registry) || registry.length === 0) {
  return { success: false, error: 'registry_empty', trace_id: $input.item.json.trace_id };
}

const connector = registry.find(c => c.id === connectorId);

if (!connector || !connector.mock?.schema) {
  return {
    success: true,
    warning: 'No schema defined for validation',
    connector_id: connectorId,
    trace_id: $input.item.json.trace_id
  };
}

const schemaPath = connector.mock.schema.startsWith('./') 
  ? path.join(schemaBasePath, connector.mock.schema.substring(2))
  : path.join(schemaBasePath, connector.mock.schema);

if (!fs.existsSync(schemaPath)) {
  return {
    success: false,
    error: 'Schema file not found',
    schema_path: schemaPath,
    trace_id: $input.item.json.trace_id
  };
}

const schema = JSON.parse(fs.readFileSync(schemaPath, 'utf8'));

const errors = [];
const checkRequired = (obj, schemaObj, path = '') => {
  if (schemaObj.required && Array.isArray(schemaObj.required)) {
    for (const field of schemaObj.required) {
      if (!(field in obj)) {
        errors.push(`Missing required field: ${path}${field}`);
      }
    }
  }

  if (schemaObj.properties) {
    for (const [key, propSchema] of Object.entries(schemaObj.properties)) {
      if (obj[key] && propSchema.type === 'object' && propSchema.properties) {
        checkRequired(obj[key], propSchema, `${path}${key}.`);
      }
    }
  }
};

checkRequired(mockData, schema);

return {
  success: errors.length === 0,
  connector_id: connectorId,
  endpoint: endpoint,
  schema_conformance: errors.length === 0 ? 100 : 50,
  validation_errors: errors,
  trace_id: $input.item.json.trace_id
};

const fs = require('fs');
const path = require('path');

const mockBasePath = process.env.MOCK_BASE_PATH || '/data/Aigent_Modules_Core/mocks';

const indexPath = path.join(mockBasePath, 'index.json');

if (!fs.existsSync(indexPath)) {
  const files = fs.readdirSync(mockBasePath)
    .filter(f => f.endsWith('.json') && f !== 'index.json');

  const index = files.map(f => {
    const parts = f.replace('.json', '').split('_');
    return {
      connector_id: parts.slice(0, -1).join('_'),
      endpoint: parts[parts.length - 1],
      filename: f
    };
  });

  return {
    success: true,
    mocks: index,
    count: index.length,
    auto_generated: true,
    trace_id: $input.item.json.trace_id
  };
}

const index = JSON.parse(fs.readFileSync(indexPath, 'utf8'));

return {
  success: true,
  mocks: index,
  count: index.length,
  trace_id: $input.item.json.trace_id
};
