const body = $input.item.json.body || {};
const trace_id = `TEST-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`;
return {
  test_mode: body.test_mode || 'all',
  connector_id: body.connector_id || null,
  trace_id,
  timestamp: new Date().toISOString(),
  start_time: Date.now()
};

const trace_id = $input.item.json.trace_id;
const start_time = $input.item.json.start_time;

try {
  const baseUrl = process.env.N8N_BASE_URL || "http://localhost:5678";

  const loadResponse = await this.helpers.httpRequest({
    method: 'POST',
    url: `${baseUrl}/webhook/eae3109b-f891-42bb-9165-7c12aaeb9071/connector-manager/load`,
    body: { operation: 'load' },
    json: true,
    timeout: 5000
  });

  if (!loadResponse || !loadResponse.success) {
    return {
      success: false,
      error: 'registry_load_failed',
      details: loadResponse,
      trace_id,
      start_time
    };
  }

  return {
    success: true,
    connectors: loadResponse.connectors || [],
    connector_count: loadResponse.connector_count || 0,
    trace_id,
    start_time
  };

} catch (error) {
  return {
    success: false,
    error: 'registry_load_failed',
    error_message: error.message,
    trace_id,
    start_time
  };
}

const loadResult = $input.item.json;

if (!loadResult.success) {
  return loadResult;
}

const connectors = loadResult.connectors || [];
if (connectors.length === 0) {
  return { success: false, error: 'registry_empty', trace_id: loadResult.trace_id };
}

const metadata = $('Add Metadata').first()?.json;
const testMode = metadata?.test_mode || 'all';
const targetId = metadata?.connector_id;
let testTargets = [];

if (testMode === 'single' && targetId) {
  const connector = connectors.find(c => c.id === targetId);
  if (connector) testTargets = [connector];
} else {
  const underTest = process.env.CONNECTOR_UNDER_TEST;
  if (underTest) {
    const connector = connectors.find(c => c.id === underTest);
    if (connector) testTargets = [connector];
  } else {
    testTargets = connectors;
  }
}

return {
  test_targets: testTargets,
  total_connectors: testTargets.length,
  trace_id: loadResult.trace_id,
  start_time: loadResult.start_time
};

const testTargets = $input.item.json.test_targets || [];
const trace_id = $input.item.json.trace_id;
const start_time = $input.item.json.start_time;
return testTargets.map(connector => ({ connector, trace_id, start_time }));

const connector = $input.item.json?.connector;

if (!connector || !connector.id || !connector.endpoints) {
  return {
    connector_id: 'unknown',
    connector_name: 'unknown',
    endpoints_tested: 0,
    test_results: [],
    tests_passed: 0,
    tests_failed: 0,
    error: 'Invalid connector data',
    trace_id: $input.item.json?.trace_id || 'unknown'
  };
}

const endpoints = Object.keys(connector.endpoints);
const testResults = [];

const baseUrl = process.env.N8N_BASE_URL || "http://localhost:5678";
const uuid11A = "eae3109b-f891-42bb-9165-7c12aaeb9071";
const uuid11B = "365b6343-cc32-4ac8-837f-bde02317aa9a";

for (const endpoint of endpoints) {

  const mockStart = Date.now();
  let mockResult = {};

  try {
    const mockResponse = await this.helpers.httpRequest({
      method: 'POST',
      url: `${baseUrl}/webhook/${uuid11B}/connector-mock/mock-fetch`,
      body: { connector_id: connector.id, endpoint },
      json: true,
      timeout: 5000
    });

    mockResult = {
      success: mockResponse.success !== false,
      duration_ms: Date.now() - mockStart,
      source: 'mock',
      error: mockResponse.error || null
    };

  } catch (err) {
    mockResult = {
      success: false,
      error: err.message,
      source: 'mock',
      duration_ms: Date.now() - mockStart
    };
  }

  let liveResult = null;
  const mockModeGlobal = (process.env.MOCK_MODE_GLOBAL || 'false').toLowerCase() === 'true';

  if (!mockModeGlobal) {
    const liveStart = Date.now();

    try {
      const liveResponse = await this.helpers.httpRequest({
        method: 'POST',
        url: `${baseUrl}/webhook/${uuid11A}/connector-manager/execute`,
        body: {
          connector_id: connector.id,
          endpoint,
          payload: {}
        },
        json: true,
        timeout: parseInt(process.env.DEFAULT_TIMEOUT_MS || '10000')
      });

      liveResult = {
        success: liveResponse.success !== false,
        duration_ms: Date.now() - liveStart,
        source: 'live',
        error: liveResponse.error || null
      };

    } catch (err) {
      liveResult = {
        success: false,
        error: err.message,
        source: 'live',
        duration_ms: Date.now() - liveStart
      };
    }
  }

  testResults.push({
    endpoint,
    mock: mockResult,
    live: liveResult,
    schema_check: "skipped"
  });
}

const totalTests = testResults.length * 2;
const passedTests = testResults.reduce(
  (s, r) => s +
    (r.mock.success ? 1 : 0) +
    (r.live?.success ? 1 : 0),
  0
);

return {
  connector_id: connector.id,
  connector_name: connector.name,
  endpoints_tested: endpoints.length,
  test_results: testResults,
  tests_passed: passedTests,
  tests_failed: totalTests - passedTests,
  trace_id: $input.item.json?.trace_id || 'unknown'
};

const allResults = $input.all().map(i => i.json) || [];
const firstItem = $input.first()?.json || {};

const totalConnectors = allResults.length;
const totalTests = allResults.reduce((sum, r) => sum + (r.tests_passed || 0) + (r.tests_failed || 0), 0);
const totalPassed = allResults.reduce((sum, r) => sum + (r.tests_passed || 0), 0);
const totalFailed = allResults.reduce((sum, r) => sum + (r.tests_failed || 0), 0);

let totalLatency = 0;
let totalEndpoints = 0;
for (const r of allResults) {
  for (const t of (r.test_results || [])) {
    if (t.mock?.duration_ms) {
      totalLatency += t.mock.duration_ms;
      totalEndpoints++;
    }
    if (t.live?.duration_ms) {
      totalLatency += t.live.duration_ms;
      totalEndpoints++;
    }
  }
}
const avgLatency = totalEndpoints > 0 ? Math.round(totalLatency / totalEndpoints) : 0;
const duration = Date.now() - (firstItem.start_time || Date.now());

const summary = {
  success: totalFailed === 0,
  trace_id: firstItem.trace_id || 'unknown',
  timestamp: new Date().toISOString(),
  duration_ms: duration,
  average_latency_ms: avgLatency,
  total_connectors: totalConnectors,
  total_tests: totalTests,
  tests_passed: totalPassed,
  tests_failed: totalFailed,
  success_rate: totalTests > 0 ? ((totalPassed / totalTests) * 100).toFixed(1) : '0.0',
  detailed_results: allResults
};

return summary;

const fs = require('fs');
const path = require('path');
const results = $input.item.json || {};
const cachePath = process.env.CACHE_PATH || '/data/Aigent_Modules_Core/cache';

if (!fs.existsSync(cachePath)) {
  fs.mkdirSync(cachePath, { recursive: true });
}

const cacheFile = path.join(cachePath, 'last_test_results.json');
fs.writeFileSync(cacheFile, JSON.stringify(results, null, 2), 'utf8');

const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
const historyFile = path.join(cachePath, `test_results_${timestamp}.json`);
fs.writeFileSync(historyFile, JSON.stringify(results, null, 2), 'utf8');

return { ...results, cache_status: 'written', cache_file: cacheFile, history_file: historyFile };
