// ENTERPRISE: API Key Authentication
const input = $input.item.json;
const headers = input.headers || {};

const apiKeyEnabled = $env.API_KEY_ENABLED === 'true';
const expectedApiKey = $env.API_KEY;

if (apiKeyEnabled && expectedApiKey) {
  const providedKey = headers['x-api-key'] || headers['authorization']?.replace('Bearer ', '');

  if (!providedKey || providedKey !== expectedApiKey) {
    return {
      _auth_failed: true,
      success: false,
      error: 'Unauthorized: Invalid or missing API key',
      timestamp: new Date().toISOString()
    };
  }
}

return { ...input, _auth_passed: true };

// ENTERPRISE: Rich execution metadata + duplicate check
const executionStartTime = Date.now();
const input = $input.item.json;
const headers = input.headers || {};

const clientIP = headers['x-forwarded-for']?.split(',')[0].trim() || headers['x-real-ip'] || 'unknown';

const traceId = `PAY-${executionStartTime}-${Math.random().toString(36).substring(7)}`;

let body = input.body || {};
if (Object.keys(body).length === 0 && input.amount) {
  body = {
    amount: input.amount,
    customer_email: input.customer_email,
    description: input.description,
    currency: input.currency,
    booking_id: input.booking_id
  };
}

// Simple duplicate check (same email + amount in last 60 seconds)
const duplicateKey = `${body.customer_email}-${body.amount}-${Math.floor(Date.now() / 60000)}`;

return {
  body: body,
  _metadata: {
    trace_id: traceId,
    execution_start_time: executionStartTime,
    client_ip: clientIP,
    duplicate_key: duplicateKey,
    workflow_version: 'enterprise-1.0.0',
    module: 'billing_payments',
    timestamp: new Date().toISOString()
  }
};

// ENTERPRISE: PHI masking

// ENTERPRISE: Body unwrapper for PowerShell/n8n compatibility
function unwrapBody(input) {
  if (input.body && input.body.body && typeof input.body.body === 'object') {
    return input.body.body;
  }
  if (input.body && typeof input.body === 'object') {
    return input.body;
  }
  return input;
}

const body = unwrapBody($input.item.json);
const metadata = $input.item.json._metadata;

const emailNormalized = (body.customer_email || '').toLowerCase().trim();

const maskEmail = (email) => {
  if (!email || !email.includes('@')) return email;
  const [local, domain] = email.split('@');
  return local[0] + '***' + (local[local.length-1] || '') + '@' + domain;
};

return {
  _metadata: metadata,
  data: {
    trace_id: metadata.trace_id,
    amount: parseInt(body.amount),
    currency: (body.currency || 'usd').toLowerCase(),
    customer_email: emailNormalized,
    description: body.description,
    booking_id: body.booking_id || null,
    client_ip: metadata.client_ip,
    duplicate_key: metadata.duplicate_key,
    timestamp: metadata.timestamp
  },
  data_masked: {
    customer_email: maskEmail(emailNormalized),
    amount: parseInt(body.amount),
    description: body.description
  }
};

