// ENTERPRISE: Calculate execution time and format response
const metadata = $('Normalize & Mask Data').first().json._metadata;
const data = $('Normalize & Mask Data').first().json.data;
const charge = $input.item.json;

const executionDuration = Date.now() - metadata.execution_start_time;

let performanceCategory = 'fast';
if (executionDuration >= 2000) performanceCategory = 'slow';
else if (executionDuration >= 1000) performanceCategory = 'normal';

return {
  success: true,
  message: 'Payment processed successfully',
  data: {
    charge_id: charge.id,
    amount: charge.amount,
    currency: charge.currency,
    status: charge.status,
    receipt_url: charge.receipt_url,
    trace_id: data.trace_id
  },
  metadata: {
    execution_time_ms: executionDuration,
    performance: performanceCategory,
    workflow_version: 'enterprise-1.0.0',
    timestamp: metadata.timestamp
  }
};

// ENTERPRISE: Format payment declined error
const charge = $input.item.json;

return {
  success: false,
  error: 'Payment declined',
  details: {
    charge_id: charge.id,
    failure_code: charge.failure_code,
    failure_message: charge.failure_message || 'Payment could not be processed',
    decline_code: charge.outcome?.network_status || 'unknown'
  },
  timestamp: new Date().toISOString()
};

// ENTERPRISE: Detailed validation errors

// ENTERPRISE: Body unwrapper for PowerShell/n8n compatibility
function unwrapBody(input) {
  if (input.body && input.body.body && typeof input.body.body === 'object') {
    return input.body.body;
  }
  if (input.body && typeof input.body === 'object') {
    return input.body;
  }
  return input;
}

const body = unwrapBody($input.item.json);
const errors = [];

if (!body.amount || body.amount <= 0) {
  errors.push('amount (must be greater than 0)');
} else if (body.amount > 100000) {
  errors.push('amount (maximum $1000.00 per transaction)');
}

if (!body.customer_email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(body.customer_email)) {
  errors.push('customer_email (valid email required)');
}

if (!body.description) {
  errors.push('description (required)');
}

const errorMessage = errors.length > 0 
  ? `Missing or invalid required fields: ${errors.join(', ')}`
  : 'Please provide amount, customer_email, and description';

return {
  success: false,
  error: errorMessage,
  validation_errors: errors,
  timestamp: new Date().toISOString()
};
